import React from 'react'
import { Link } from 'react-router-dom'

const {
  Stitch,
  RemoteMongoClient,
  AnonymousCredential
} = require('mongodb-stitch-browser-sdk');

class Login extends React.Component {

loginSubmit()
{
  this.props.history.pop()
  this.props.history.push('auth')
}
componentDidMount()
{

const client = Stitch.initializeDefaultAppClient('internship-shixg');

const db = client.getServiceClient(RemoteMongoClient.factory, 'mongodb-atlas').db('<DATABASE>');

client.auth.loginWithCredential(new AnonymousCredential()).then(user =>
db.collection('<COLLECTION>').updateOne({owner_id: client.auth.user.id}, {$set:{number:42}}, {upsert:true})
).then(() =>
db.collection('<COLLECTION>').find({owner_id: client.auth.user.id}, { limit: 100}).asArray()
).then(docs => {
  console.log("Found docs", docs)
  console.log("[MongoDB Stitch] Connected to Stitch")
}).catch(err => {
  console.error(err)
});
}

  render () {

    return <div className ="pb_cover_v3 overflow-hidden cover-bg-indigo cover-bg-opacity text-left pb_gradient_v1 pb_slant-light">
   <div className="container m-auto ">
        <form className ="bg-white rounded pb_form_v1">
          <h2>Log In  For Free</h2>
          <br/>
          <div >

            <input
              type="email"
              className="form-control pb_height-50 reverse"
              placeholder ="Email"
            />
            <br/>
          </div>
          <div>

            <input
              type="password"
              className="form-control pb_height-50 reverse"
              placeholder ="Password"
            ></input>

          </div>

         <br/>
          <div className="form-group">
          <button type="submit" onClick ={()=>{this.loginSubmit()}} className="btn btn-primary btn-lg btn-block pb_btn-pill  btn-shadow-blue">
            Submit
          </button><br/>
          <label >New to the Site  </label><Link to ="/signup"> <label  className ="link-color">  Sign Up </label></Link>
          </div>
        </form>
      </div>
    </div>
  }
}
export default Login
